import React, { Component } from 'react';

class App extends Component {
  constructor() {
    super();
    this.state = { displayBio: false };
  }

  render() {
    let bio = (
      <div>
        <p>My favorite language is JavaScript</p>
        <p>I love React!</p>
      </div>
    );

    if (!this.state.displayBio) {
      bio = null;
    }

    return (
      <div>
        <h1>Hello!</h1>
        <p>My name is Johnny!</p>
        <p>I love life!</p>
        {bio}
      </div>
    )
  } 
};

export default App;
